INSERT INTO HelpUsDB.organizacions (nombre,resumen,telefono,enlace,imagen)
VALUES ('Coordinadora Nacional de Ayuda a Mujeres Indígenas','Especializadas en la ayuda para mujeres indígenas. Coadyuvan en el desarrollo de la mujer de otras comunidades.','55-32-29-23','https://www.facebook.com/mujeresindigenasconamimexico/','organizacion_1.JPG'),('Casa Semillas','Fondo Semillas es una organización feminista que mejora la vida de las mujeres en México. Soñamos con un país donde todas las mujeres, indígenas, mestizas, negras, jóvenes, migrantes, heterosexuales, lesbianas, madres, estudiantes, tengan acceso a la salud, al trabajo digno, a tomar sus propias decisiones, a la justicia y a la felicidad.','5553-2900 y 5553-0109','https://semillas.org.mx/','organizacion_2.png'), ('Puerta Violeta','Tiene el objetivo de proteger y brindar apoyo especializado a mujeres víctimas de violencia.','','','organizacion_3.jpg'), ('Asociación para el Desarrollo Integral de Personas Violadas A.C.','Imparten cursos psicológicos y legales, en caso de que hayas sido víctima de un delito sexual o quieras prevenir serlo.','5682-7969 y 5547-8639','http://www.adivac.org/','organizacion_4.jpg'), ('Fundación en movimiento','Fundación en Movimiento es una Asociación Civil que tiene como misión: “Erradicar y prevenir el bullying promoviendo la Educación para la Paz”. Además, se caracteriza por el trabajo que realiza basado en los valores de la generosidad, el respeto y la responsabilidad.','+52 (55) 1327-8123, 1327-0112 y 1327-0113','http://www.fundacionenmovimiento.org.mx/','organizacion_5.jpg'),('NACE Asociación No al Acoso Escolar','La Asociación No al Acoso Escolar la componemos psicólogos, profesores, abogados y especialistas en las diferentes áreas que tienen que ver con el acoso escolar y la atención a las personas que se ven involucradas en una situación de bullying.\r\n\r\nLos integrantes de NACE estamos seriamente comprometidos con las víctimas de acoso escolar y sus familias.','622 326 237','https://www.noalacoso.org/contacto/','organizacion_6.jpg');
